import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-diretivas-estruturais',
  templateUrl: './diretivas-estruturais.component.html',
  styleUrls: ['./diretivas-estruturais.component.scss']
})
export class DiretivasEstruturaisComponent implements OnInit{

  public xuxa: boolean = true;
  public conditionClick: boolean = true;

  public list: Array<{ nome: string }> = [
    { nome: "Edan"}, 
    { nome: "Ju"},
    { nome: "Helena"},
    { nome: "Edson"}
  ];

  constructor() {}

  ngOnInit(): void {
    setInterval( ()=>{
      if(this.xuxa) {
        this.xuxa = false;
      } else {
        this.xuxa = true;
      }
    }, 2000)
  }

  public onClick() {
    if(this.conditionClick) {
      this.conditionClick = false;
    } else {
      this.conditionClick = true;
    }
  }

  public onClickAddList() {
    this.list.push({nome: "Lice"});
  }

  public onCLickRemoveList() {
    this.list.pop();
  }

}
<h2>ngIf</h2>
<p *ngIf="xuxa; else elseBlock">TRUE</p>
<ng-template #elseBlock>
    <p>FALSE</p>
</ng-template>

<br>
<p *ngIf="conditionClick">TRUE</p>

<p *ngIf="!conditionClick">FALSE</p>

<button (click)="onClick()">PRESS</button>

<hr>
<h2>ngFor</h2>
<ul>
    <li *ngFor="let item of list">
        {{item.nome}}
    </li>
</ul>

<button style="margin-right: 10px;" (click)="onClickAddList()">ADD</button>
<button (click)="onCLickRemoveList()">REMOVE</button>
